import networkx as nx
import matplotlib.pyplot as plt

def draw_tree(adjacency_dict):
    # Create a directed graph
    G = nx.DiGraph()

    # Add nodes and edges based on the adjacency dictionary
    for parent, children in adjacency_dict.items():
        for child in children:
            G.add_edge(parent, child)

    # Use graphviz_layout with dot for hierarchical layout
    pos = nx.nx_agraph.graphviz_layout(G, prog="dot")

    # Draw the tree with a downward layout
    plt.figure(figsize=(10, 7))
    nx.draw(G, pos, with_labels=True, arrows=False, node_size=2000, 
            node_color="lightblue", font_size=10, font_weight="bold")
    plt.title("Tree Diagram from Adjacency Dictionary (Top-Down)")
    plt.gca().invert_yaxis()  # Ensure the tree points downward
    plt.show()
    
